	<script>
		{
			setTimeout(() => {

				const DOM = {};
				DOM.body = document.body;
				DOM.gridElems = Array.from(document.querySelectorAll('.grid'));

				imagesLoaded(document.querySelectorAll('.grid__item'), {
					background: true
				}, () => {
					DOM.body.classList.remove('loading');
					new Slideshow(DOM.gridElems, {
						filledColor: '#fff'
					});
				});

			}, 3000);
		}
	</script>
	
